local Matcher = require("Common.entitas.Matcher")local GroupEvent = require("Common.entitas.GroupEvent")local Player_comps = require('.PlayerComponents')local PlayerMatcher = require('.PlayerMatchers')local tabledeepcopy = table.deepcopy---@class AnyNameADDEDListenerEventSystem : entitas.ReactiveSystemlocal M = class({},'AnyNameADDEDListenerEventSystem', classMap.ReactiveSystem)function M:Ctor(context)    M.__base.Ctor(self, context)    self._listeners = context:get_group(PlayerMatcher.AnyNameADDEDListener)endfunction M:get_trigger()    return {        {            Matcher({Player_comps.Name}),            GroupEvent.GroupEvent.ADDED        }    }endfunction M:filter(entity)    return entity:hasName()endfunction M:execute(es)    local buffer = self._listeners:get_entity_buffer()    es:foreach( function( e  )        local comp = e.name        for _, entity in pairs(buffer) do            local list = entity.anyNameADDEDListener.value:get_bufferkv()            for k ,v in pairs(list) do                k(v, e , comp.value );            end        end    end)endreturn M