local Matcher = require("Common.entitas.Matcher")local GroupEvent = require("Common.entitas.GroupEvent")local Player_comps = require('.PlayerComponents')local PlayerMatcher = require('.PlayerMatchers')local tabledeepcopy = table.deepcopy---@class NameADDEDListenerEventSystem : entitas.ReactiveSystemlocal M = class({},'NameADDEDListenerEventSystem', classMap.ReactiveSystem)function M:Ctor(context)    M.__base.Ctor(self, context)endfunction M:get_trigger()    return {        {            Matcher({Player_comps.Name}),            GroupEvent.GroupEvent.ADDED        }    }endfunction M:filter(entity)    return entity:hasName() and entity:hasNameADDEDListener()endfunction M:execute(es)    es:foreach( function( e  )        local comp = e.name        local list = entity.nameADDEDListener.value:get_bufferkv()        for k ,v in pairs(list) do            k(v, e , comp.value );        end    end)endreturn M